import React, { useState, useEffect, useRef } from 'react';
import Webcam from 'react-webcam';
import './App.css';

function App() {
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const [isDetecting, setIsDetecting] = useState(false);
  const [detectionResult, setDetectionResult] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const wsRef = useRef(null);

  useEffect(() => {
    connectWebSocket();
    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, []);

  const connectWebSocket = () => {
    const ws = new WebSocket('ws://localhost:8001/ws/detection');

    ws.onopen = () => {
      console.log('WebSocket connected');
      setIsConnected(true);
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'detection_result') {
        setDetectionResult(data);
        drawLandmarks(data.landmarks);
      }
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      setIsConnected(false);
    };

    ws.onclose = () => {
      console.log('WebSocket disconnected');
      setIsConnected(false);
    };

    wsRef.current = ws;
  };

  const drawLandmarks = (landmarks) => {
    if (!landmarks || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const video = webcamRef.current.video;

    if (!video) return;

    // Set canvas size to match video
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Draw landmarks
    landmarks.forEach((landmark, index) => {
      const x = landmark.x * canvas.width;
      const y = landmark.y * canvas.height;

      // Draw landmark point
      ctx.beginPath();
      ctx.arc(x, y, 5, 0, 2 * Math.PI);
      ctx.fillStyle = '#FF0000'; // Red color
      ctx.fill();

      // Draw landmark number
      ctx.fillStyle = '#FFFFFF';
      ctx.font = '12px Arial';
      ctx.fillText(index.toString(), x + 8, y - 8);
    });

    // Draw connections between landmarks
    const connections = [
      [0, 1], [1, 2], [2, 3], [3, 4], // Thumb
      [0, 5], [5, 6], [6, 7], [7, 8], // Index finger
      [0, 9], [9, 10], [10, 11], [11, 12], // Middle finger
      [0, 13], [13, 14], [14, 15], [15, 16], // Ring finger
      [0, 17], [17, 18], [18, 19], [19, 20], // Pinky
      [0, 5], [5, 9], [9, 13], [13, 17] // Palm connections
    ];

    ctx.strokeStyle = '#00FF00'; // Green color
    ctx.lineWidth = 2;

    connections.forEach(([start, end]) => {
      if (landmarks[start] && landmarks[end]) {
        const startX = landmarks[start].x * canvas.width;
        const startY = landmarks[start].y * canvas.height;
        const endX = landmarks[end].x * canvas.width;
        const endY = landmarks[end].y * canvas.height;

        ctx.beginPath();
        ctx.moveTo(startX, startY);
        ctx.lineTo(endX, endY);
        ctx.stroke();
      }
    });
  };

  const getAccuracyColor = (accuracy) => {
    if (accuracy >= 0.9) return '#00FF00'; // Green
    if (accuracy >= 0.7) return '#FFFF00'; // Yellow
    if (accuracy >= 0.5) return '#FFA500'; // Orange
    return '#FF0000'; // Red
  };

  const getConfidenceColor = (confidence) => {
    if (confidence >= 0.9) return '#00FF00'; // Green
    if (confidence >= 0.7) return '#FFFF00'; // Yellow
    if (confidence >= 0.5) return '#FFA500'; // Orange
    return '#FF0000'; // Red
  };

  const toggleDetection = () => {
    if (isDetecting) {
      setIsDetecting(false);
    } else {
      setIsDetecting(true);
      startDetection();
    }
  };

  const startDetection = () => {
    if (!isDetecting) return;

    const captureFrame = () => {
      if (webcamRef.current && wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
        const imageSrc = webcamRef.current.getScreenshot();
        if (imageSrc) {
          wsRef.current.send(JSON.stringify({
            type: 'frame',
            image: imageSrc
          }));
        }
      }

      if (isDetecting) {
        setTimeout(captureFrame, 100); // 10 FPS
      }
    };

    captureFrame();
  };

  useEffect(() => {
    if (isDetecting) {
      startDetection();
    }
  }, [isDetecting]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Sign Language Detection</h1>
        <div className="status">
          Status: {isConnected ? 'üü¢ Connected' : 'üî¥ Disconnected'}
        </div>
      </header>

      <main className="App-main">
        <div className="camera-container">
          <div className="video-wrapper">
            <Webcam
              ref={webcamRef}
              audio={false}
              screenshotFormat="image/jpeg"
              videoConstraints={{
                width: 640,
                height: 480,
                facingMode: 'user'
              }}
            />
            <canvas
              ref={canvasRef}
              className="landmarks-canvas"
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                pointerEvents: 'none'
              }}
            />
          </div>

          <div className="controls">
            <button 
              onClick={toggleDetection}
              className={`detection-btn ${isDetecting ? 'active' : ''}`}
            >
              {isDetecting ? 'Stop Detection' : 'Start Detection'}
            </button>
          </div>
        </div>

        <div className="results">
          <h2>Detection Results</h2>
          {detectionResult ? (
            <div className="result-content">
              <div className="result-item">
                <strong>Hands Detected:</strong> {detectionResult.hands_detected ? 'Yes' : 'No'}
              </div>

              {detectionResult.sign && (
                <div className="result-item">
                  <strong>Sign:</strong> 
                  <span className="sign-display">{detectionResult.sign}</span>
                </div>
              )}

              {detectionResult.phrase && (
                <div className="result-item phrase-item">
                  <strong>Phrase:</strong> 
                  <span className="phrase-display">{detectionResult.phrase}</span>
                </div>
              )}

              {detectionResult.confidence > 0 && (
                <div className="result-item">
                  <strong>Confidence:</strong> 
                  <span style={{ color: getConfidenceColor(detectionResult.confidence) }}>
                    {(detectionResult.confidence * 100).toFixed(1)}%
                  </span>
                </div>
              )}

              {detectionResult.accuracy > 0 && (
                <div className="result-item">
                  <strong>Accuracy:</strong> 
                  <span style={{ color: getAccuracyColor(detectionResult.accuracy) }}>
                    {(detectionResult.accuracy * 100).toFixed(1)}%
                  </span>
                </div>
              )}

              <div className="result-item">
                <strong>Landmarks:</strong> {detectionResult.landmarks.length}
              </div>
            </div>
          ) : (
            <p>No detection results yet. Start detection to see results.</p>
          )}
        </div>

        <div className="instructions">
          <h3>Try These Signs:</h3>
          <div className="sign-examples">
            <div className="sign-example">
              <strong>A:</strong> Make a fist
            </div>
            <div className="sign-example">
              <strong>B:</strong> Point with index finger
            </div>
