import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Camera from './components/Camera';
import SignDisplay from './components/SignDisplay';
import AccuracyMeter from './components/AccuracyMeter';
import { Camera as CameraIcon, BarChart3, Settings, Home } from 'lucide-react';

function App() {
  const [sessionId, setSessionId] = useState(null);
  const [detectionResults, setDetectionResults] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // Create a new session when the app starts
    createSession();
  }, []);

  const createSession = async () => {
    try {
      const response = await fetch('/api/sessions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      setSessionId(data.session_id);
    } catch (error) {
      console.error('Failed to create session:', error);
    }
  };

  const handleDetectionResult = (result) => {
    setDetectionResults(result);
  };

  const handleConnectionStatus = (status) => {
    setIsConnected(status);
  };

  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center h-16">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <h1 className="text-2xl font-bold text-gradient">
                    Sign Language Detector
                  </h1>
                </div>
              </div>

              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-2">
                  <div className={`w-3 h-3 rounded-full ${isConnected ? 'bg-green-500' : 'bg-red-500'}`}></div>
                  <span className="text-sm text-gray-600">
                    {isConnected ? 'Connected' : 'Disconnected'}
                  </span>
                </div>

                <nav className="flex space-x-4">
                  <button className="p-2 text-gray-600 hover:text-primary-600 transition-colors">
                    <Home size={20} />
                  </button>
                  <button className="p-2 text-gray-600 hover:text-primary-600 transition-colors">
                    <BarChart3 size={20} />
                  </button>
                  <button className="p-2 text-gray-600 hover:text-primary-600 transition-colors">
                    <Settings size={20} />
                  </button>
                </nav>
              </div>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <Routes>
            <Route path="/" element={
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Camera Section */}
                <div className="lg:col-span-2">
                  <div className="card">
                    <div className="flex items-center justify-between mb-4">
                      <h2 className="text-xl font-semibold text-gray-900 flex items-center">
                        <CameraIcon className="mr-2" size={24} />
                        Live Camera Feed
                      </h2>
                      <div className="text-sm text-gray-500">
                        Session: {sessionId ? sessionId.slice(0, 8) : 'Loading...'}
                      </div>
                    </div>
                    <Camera 
                      sessionId={sessionId}
                      onDetectionResult={handleDetectionResult}
                      onConnectionStatus={handleConnectionStatus}
                    />
                  </div>
                </div>

                {/* Results Section */}
                <div className="space-y-6">
                  {/* Sign Display */}
                  <div className="card">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">
                      Detected Sign
                    </h3>
                    <SignDisplay result={detectionResults} />
                  </div>

                  {/* Accuracy Meter */}
                  <div className="card">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">
                      Accuracy Score
                    </h3>
                    <AccuracyMeter result={detectionResults} />
                  </div>

                  {/* Instructions */}
                  <div className="card">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">
                      How to Use
                    </h3>
                    <div className="space-y-3 text-sm text-gray-600">
                      <div className="flex items-start">
                        <div className="w-2 h-2 bg-primary-500 rounded-full mt-2 mr-3 flex-shrink-0"></div>
                        <p>Position your hand clearly in front of the camera</p>
                      </div>
                      <div className="flex items-start">
                        <div className="w-2 h-2 bg-primary-500 rounded-full mt-2 mr-3 flex-shrink-0"></div>
                        <p>Make ASL alphabet signs (A-Z)</p>
                      </div>
                      <div className="flex items-start">
                        <div className="w-2 h-2 bg-primary-500 rounded-full mt-2 mr-3 flex-shrink-0"></div>
                        <p>Keep your hand steady for better accuracy</p>
                      </div>
                      <div className="flex items-start">
                        <div className="w-2 h-2 bg-primary-500 rounded-full mt-2 mr-3 flex-shrink-0"></div>
                        <p>Ensure good lighting for optimal detection</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            } />
          </Routes>
        </main>

        {/* Footer */}
        <footer className="bg-white border-t border-gray-200 mt-16">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
            <div className="text-center text-sm text-gray-500">
              <p>Sign Language Detection System v1.0.0</p>
              <p className="mt-1">Powered by Computer Vision & Machine Learning</p>
            </div>
          </div>
        </footer>
      </div>
    </Router>
  );
}

export default App;
